import React, { useEffect, useState } from 'react';
import './styles/globals.css';
import { useNotification } from './components/Notification';
import DealDetails from './pages/app/DealDetails';
import CommitInvestment from './pages/app/CommitInvestment';

// Simple Loading Component
const LoadingSpinner = () => {
  return (
    <div className="flex items-center justify-center min-h-screen bg-primary px-4 sm:px-6">
      <div className="w-10 h-10 border-4 border-gray-700 border-t-accent rounded-full animate-spin-slow"></div>
    </div>
  );
};

// GetStarted Component
const GetStarted = () => {
  const handleGetStarted = () => {
    window.history.pushState({}, '', '/phone-number');
    window.location.reload();
  };

  return (
    <div className="min-h-screen bg-black flex flex-col justify-end items-center p-4 sm:p-6 lg:p-8 text-white safe-area-padding">
      <div className="w-full max-w-sm sm:max-w-md lg:max-w-lg flex flex-col items-center">
        <h1 className="text-3xl sm:text-4xl lg:text-5xl font-bold mb-8 sm:mb-12 lg:mb-16 text-center font-tiempos leading-tight">
          Invest in What's Next.
        </h1>

        <button 
          onClick={handleGetStarted}
          className="bg-white text-black border-none py-4 px-6 sm:py-5 sm:px-8 text-base sm:text-lg font-medium rounded cursor-pointer w-full flex items-center justify-center gap-2 mb-3 touch-target hover:bg-gray-100 transition-colors duration-200 active:bg-gray-200"
        >
          Get started
        </button>
      </div>
    </div>
  );
};

// PhoneNumber Component
interface PageProps {
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
}

const PhoneNumber: React.FC<PageProps> = ({ showNotification }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [invitationCode, setInvitationCode] = useState('');

  const handleSubmit = async (e: any) => {
    e.preventDefault();
    if (phoneNumber.length !== 10) {
              showNotification('Please enter a valid 10-digit phone number', 'error');
      return;
    }

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/send?phone_number=${phoneNumber}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        localStorage.setItem('phoneNumber', phoneNumber);
        localStorage.setItem('invitationCode', invitationCode);
        if (data.user_id) {
          localStorage.setItem('userId', data.user_id);
        }
        window.history.pushState({}, '', '/verify-phone-otp');
        window.location.reload();
      } else {
        showNotification(data.message || 'Failed to send OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to send OTP. Please check your internet connection.', 'error');
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/get-started');
    window.location.reload();
  };

  const handlePhoneNumberChange = (e: any) => {
    const value = e.target.value.replace(/\D/g, '');
    if (value.length <= 10) {
      setPhoneNumber(value);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary to-gray-800 flex flex-col justify-center text-white p-4 sm:p-6 lg:p-8 safe-area-padding">
      <div className="w-full max-w-full sm:max-w-md lg:max-w-lg mx-auto bg-white/5 border border-white/10 rounded-xl p-6 sm:p-8 backdrop-blur-lg animate-fade-in shadow-2xl">
        <button 
          onClick={handleBack}
          className="bg-transparent border-none text-gray-400 text-xl sm:text-2xl cursor-pointer mb-6 sm:mb-8 touch-target hover:text-white transition-all duration-300 transform hover:scale-105"
        >
          ← Back
        </button>

        <div className="animate-fade-in" style={{ animationDelay: '0.1s' }}>
          <h1 className="mb-4 sm:mb-6 text-2xl sm:text-3xl lg:text-4xl font-bold leading-tight text-center sm:text-left">
            Enter phone number
          </h1>

          <p className="text-cyan-300 mb-8 sm:mb-10 leading-relaxed text-sm sm:text-base text-center sm:text-left">
            Your number helps us verify your identity and keep your account secure.
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-8 animate-fade-in" style={{ animationDelay: '0.2s' }}>
          <div className="transform transition-all duration-300 hover:scale-[1.02]">
            <label className="block mb-3 text-gray-200 text-sm sm:text-base font-medium">
              Phone Number
            </label>
            <div className="flex items-center border border-gray-700 rounded-lg bg-gray-700 overflow-hidden focus-within:border-accent focus-within:ring-2 focus-within:ring-accent/20 transition-all duration-300">
              <span className="px-4 py-3 sm:py-4 bg-gray-600 text-gray-200 text-base sm:text-lg font-medium border-r border-gray-500">
                +91
              </span>
              <input 
                type="tel" 
                value={phoneNumber}
                onChange={handlePhoneNumberChange}
                placeholder="Enter 10-digit number"
                maxLength={10}
                className="flex-1 px-4 py-3 sm:py-4 bg-transparent border-none text-white text-base sm:text-lg outline-none placeholder-gray-400 touch-target"
                autoComplete="tel"
              />
            </div>
          </div>

          <div className="transform transition-all duration-300 hover:scale-[1.02]">
            <label className="block mb-3 text-gray-200 text-sm sm:text-base font-medium">
              Invitation Code (Optional)
            </label>
            <input 
              type="text" 
              value={invitationCode}
              onChange={(e) => setInvitationCode(e.target.value)}
              placeholder="Enter invitation code"
              className="w-full px-4 py-3 sm:py-4 border border-gray-700 rounded-lg bg-gray-700 text-white text-base sm:text-lg outline-none placeholder-gray-400 focus:border-accent focus:ring-2 focus:ring-accent/20 transition-all duration-300 touch-target"
              autoComplete="off"
            />
          </div>

          <div className="pt-4">
            <button 
              type="submit"
              disabled={phoneNumber.length !== 10}
              className={`w-full py-4 sm:py-5 px-6 sm:px-8 border-none rounded-lg text-base sm:text-lg font-semibold touch-target transition-all duration-300 transform hover:scale-[1.02] active:scale-[0.98] ${
                phoneNumber.length === 10 
                  ? 'bg-accent text-primary cursor-pointer hover:bg-green-400 active:bg-green-600 shadow-lg hover:shadow-xl' 
                  : 'bg-gray-700 text-gray-500 cursor-not-allowed opacity-60'
              }`}
            >
              <span className="flex items-center justify-center gap-2">
                Proceed
                {phoneNumber.length === 10 && (
                  <span className="animate-bounce">→</span>
                )}
              </span>
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// VerifyPhoneOTP Component - 4 digit OTP like Android
const VerifyPhoneOTP: React.FC<PageProps> = ({ showNotification }) => {
  const [otp, setOTP] = useState(['', '', '', '']);
  const phoneNumber = localStorage.getItem('phoneNumber') || '';
  const invitationCode = localStorage.getItem('invitationCode') || '';

  const handleOTPChange = (index: number, value: string) => {
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOTP = [...otp];
      newOTP[index] = value;
      setOTP(newOTP);

      if (value && index < 3) {
        const nextInput = document.getElementById(`phone-otp-${index + 1}`);
        if (nextInput) nextInput.focus();
      }
    }
  };

  const handleKeyDown = (index: number, e: any) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      const prevInput = document.getElementById(`phone-otp-${index - 1}`);
      if (prevInput) prevInput.focus();
    }
  };

  const handleSubmit = async (e: any) => {
    e.preventDefault();
    const otpValue = otp.join('');
    if (otpValue.length !== 4) {
      showNotification('Please enter complete 4-digit OTP', 'error');
      return;
    }

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/verify?phone_number=${phoneNumber}&otp=${otpValue}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      // Handle API response following Android logic
      if (!data) {
        showNotification('Failed to verify OTP. Please try again.', 'error');
        return;
      }

      if (!data.success) {
        showNotification(data.message || 'Failed to verify OTP. Please try again.', 'error');
        return;
      }

      // Show success message if provided
      if (data.message) {
        showNotification(data.message, 'success');
      }

      if (data.success) {
        // Store user ID and other data for subsequent calls
        localStorage.setItem('userId', data.user_id);
        localStorage.setItem('fundManagerId', data.fund_manager_id);

        // Debug logging
        console.log('Phone OTP verification response:', data);
        console.log('Onboarding status:', data.onboarding_status);

        // Check onboarding status (matching Android logic)
        if (data.onboarding_status === 'Completed') {
          console.log('Onboarding completed - redirecting to dashboard');
          showNotification('Welcome back! Redirecting to dashboard...', 'success');
          window.history.pushState({}, '', '/dashboard');
        } else {
          console.log('Onboarding incomplete - continuing to email entry');
          showNotification('Please complete your email verification to continue', 'info');
          window.history.pushState({}, '', '/email-entry');
        }
        window.location.reload();
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to verify OTP. Please check your internet connection.', 'error');
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/phone-number');
    window.location.reload();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary to-gray-800 flex items-center justify-center text-white p-4 sm:p-6 lg:p-8 safe-area-padding">
      <div className="w-full max-w-sm sm:max-w-md lg:max-w-lg bg-white/5 border border-white/10 rounded-xl p-6 sm:p-8 backdrop-blur-lg animate-fade-in">
        <button 
          onClick={handleBack}
          className="bg-transparent border-none text-gray-400 text-xl sm:text-2xl cursor-pointer mb-4 sm:mb-6 touch-target hover:text-white transition-colors duration-200"
        >
          ← Back
        </button>

        <h1 className="mb-4 sm:mb-6 text-2xl sm:text-3xl lg:text-4xl font-bold leading-tight">
          Verify your number
        </h1>

        <p className="text-cyan-300 mb-6 sm:mb-8 leading-relaxed text-sm sm:text-base">
          We have sent a verification code to your number +91-{phoneNumber}.
        </p>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block mb-4 text-gray-200 text-sm sm:text-base font-medium text-center">
              Enter 4-digit verification code
            </label>
            <div className="flex gap-3 sm:gap-4 justify-center">
              {otp.map((digit, index) => (
                <input
                  key={index}
                  id={`phone-otp-${index}`}
                  type="text"
                  value={digit}
                  onChange={(e) => handleOTPChange(index, e.target.value)}
                  onKeyDown={(e) => handleKeyDown(index, e)}
                  maxLength={1}
