// VerifyEmailOTP Component - 6 digit OTP like Android
const VerifyEmailOTP: React.FC<PageProps> = ({ showNotification }) => {
  const [otp, setOTP] = useState(['', '', '', '', '', '']);
  const email = localStorage.getItem('email') || '';
  const userId = localStorage.getItem('userId') || '';

  const handleOTPChange = (index: number, value: string) => {
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOTP = [...otp];
      newOTP[index] = value;
      setOTP(newOTP);

      if (value && index < 5) {
        const nextInput = document.getElementById(`email-otp-${index + 1}`);
        if (nextInput) nextInput.focus();
      }
    }
  };

  const handleKeyDown = (index: number, e: any) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      const prevInput = document.getElementById(`email-otp-${index - 1}`);
      if (prevInput) prevInput.focus();
    }
  };

  const handleSubmit = async (e: any) => {
    e.preventDefault();
    const otpValue = otp.join('');
    if (otpValue.length !== 6) {
      showNotification('Please enter complete 6-digit OTP', 'error');
      return;
    }

    try {
      const response = await fetch('https://api.fundos.services/api/v0/test/user/email/otp/verify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: userId,
          email: email,
          otp: otpValue,
        }),
      });
      const data = await response.json();

      if (data.success) {
        showNotification('Email Verified Successfully', 'success');
        window.history.pushState({}, '', '/choose-investor');
        window.location.reload();
      } else {
        showNotification('Failed to verify OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to verify OTP. Please check your internet connection.', 'error');
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/email-entry');
    window.location.reload();
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      padding: '2rem'
    }}>
      <div style={{ 
        width: '100%', 
        maxWidth: '400px',
        background: 'rgba(255, 255, 255, 0.05)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '12px',
        padding: '2rem',
        backdropFilter: 'blur(10px)'
      }}>
        <button 
          onClick={handleBack}
          style={{
            background: 'transparent',
            border: 'none',
            color: '#9ca3af',
            fontSize: '1.5rem',
            cursor: 'pointer',
            marginBottom: '1rem'
          }}
        >
          ← Back
        </button>

        <h1 style={{ 
          marginBottom: '1rem',
          fontSize: '2.5rem',
          fontWeight: 'bold'
        }}>
          Verify your Email
        </h1>

        <p style={{ 
          color: '#00ffcc', 
          marginBottom: '2rem',
          lineHeight: '1.6',
          fontSize: '14px'
        }}>
          We have sent a verification code to your email.
        </p>

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '2rem' }}>
            <label style={{
              display: 'block',
              marginBottom: '1rem',
              color: '#e5e7eb',
              fontSize: '0.9rem'
            }}>
              Enter 6-digit verification code
            </label>
            <div style={{
              display: 'flex',
              gap: '0.5rem',
              justifyContent: 'center'
            }}>
              {otp.map((digit, index) => (
                <input
                  key={index}
                  id={`email-otp-${index}`}
                  type="text"
                  value={digit}
                  onChange={(e) => handleOTPChange(index, e.target.value)}
                  onKeyDown={(e) => handleKeyDown(index, e)}
                  maxLength={1}
                  style={{
                    width: '3rem',
                    height: '3rem',
                    textAlign: 'center',
                    fontSize: '1.2rem',
                    borderRadius: '8px',
                    border: '1px solid #374151',
                    background: '#374151',
                    color: 'white',
                    outline: 'none'
                  }}
                />
              ))}
            </div>
          </div>

          <button 
            type="submit"
            disabled={otp.join('').length !== 6}
            style={{
              background: otp.join('').length === 6 ? '#00fb57' : '#374151',
              color: otp.join('').length === 6 ? '#1a1a1a' : '#6b7280',
              border: 'none',
              padding: '1rem 2rem',
              fontSize: '1rem',
              fontWeight: '600',
              borderRadius: '8px',
              cursor: otp.join('').length === 6 ? 'pointer' : 'not-allowed',
              width: '100%',
              transition: 'all 0.3s ease',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '0.5rem'
            }}
          >
            Verify →
          </button>
        </form>
      </div>
    </div>
  );
};

