// Verify Aadhaar OTP Component
const VerifyAadhaarOTP: React.FC<PageProps> = ({ showNotification }) => {
  const [otp, setOtp] = useState(['', '', '', '', '', '']);
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState('');
  const [aadhaarNumber, setAadhaarNumber] = useState('');

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    const storedAadhaar = localStorage.getItem('aadhaarNumber');
    if (storedUserId) {
      setUserId(storedUserId);
    }
    if (storedAadhaar) {
      setAadhaarNumber(storedAadhaar);
    }
  }, []);

  const handleOTPChange = (index: number, value: string) => {
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      // Auto-focus next input
      if (value && index < 5) {
        const nextInput = document.getElementById(`otp-${index + 1}`);
        if (nextInput) {
          nextInput.focus();
        }
      }
    }
  };

  const handleKeyDown = (index: number, e: any) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      const prevInput = document.getElementById(`otp-${index - 1}`);
      if (prevInput) {
        prevInput.focus();
      }
    }
  };

  const handleSubmit = async (e: any) => {
    e.preventDefault();
    const otpString = otp.join('');
    if (otpString.length !== 6) {
      showNotification('Please enter complete 6-digit OTP', 'error');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('https://api.fundos.services/api/v1/live/kyc/aadhaar/otp/verify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: userId,
          aadhaar_number: aadhaarNumber,
          otp: otpString,
        }),
      });
      const data = await response.json();

      if (data.success) {
        showNotification(data.message || 'Aadhaar verified successfully', 'success');
        localStorage.removeItem('aadhaarNumber'); // Clean up
        window.history.pushState({}, '', '/pan-verification');
        window.location.reload();
      } else {
        showNotification(data.message || 'Invalid OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to verify OTP. Please try again.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/aadhaar-verification');
    window.location.reload();
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      padding: '2rem'
    }}>
      <div style={{ 
        width: '100%', 
        maxWidth: '400px',
        background: 'rgba(255, 255, 255, 0.05)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '12px',
        padding: '2rem',
        backdropFilter: 'blur(10px)'
      }}>
        <button 
          onClick={handleBack}
          style={{
            background: 'transparent',
            border: 'none',
            color: '#9ca3af',
            fontSize: '1.5rem',
            cursor: 'pointer',
            marginBottom: '1rem'
          }}
        >
          ← Back
        </button>

        <h1 style={{
          color: '#fff',
          fontSize: '2.5rem',
          fontWeight: 'bold',
          marginBottom: '10px'
        }}>
          Verify Aadhaar OTP
        </h1>

        <p style={{
          color: '#00ffcc',
          fontSize: '14px',
          marginBottom: '2rem',
          lineHeight: '1.6'
        }}>
          Enter the 6-digit OTP sent to your Aadhaar registered mobile number.
        </p>

        <form onSubmit={handleSubmit}>
          <div style={{
            display: 'flex',
            gap: '10px',
            marginBottom: '2rem',
            justifyContent: 'center'
          }}>
            {otp.map((digit, index) => (
              <input
                key={index}
                id={`otp-${index}`}
                type="text"
                value={digit}
                onChange={(e) => handleOTPChange(index, e.target.value)}
                onKeyDown={(e) => handleKeyDown(index, e)}
                style={{
                  width: '50px',
                  height: '50px',
                  textAlign: 'center',
                  fontSize: '1.5rem',
                  fontWeight: 'bold',
                  borderRadius: '8px',
                  border: '2px solid #374151',
                  background: '#374151',
                  color: 'white',
                  outline: 'none'
                }}
                maxLength={1}
              />
            ))}
          </div>

          <button 
            type="submit"
            disabled={otp.join('').length !== 6 || loading}
            style={{
              background: (otp.join('').length === 6 && !loading) ? '#00fb57' : '#374151',
              color: (otp.join('').length === 6 && !loading) ? '#1a1a1a' : '#6b7280',
              border: 'none',
              padding: '1rem 2rem',
              fontSize: '1rem',
              fontWeight: '600',
              borderRadius: '8px',
              cursor: (otp.join('').length === 6 && !loading) ? 'pointer' : 'not-allowed',
              width: '100%',
              transition: 'all 0.3s ease'
            }}
          >
            {loading ? 'Verifying...' : 'Verify OTP'}
          </button>
        </form>
      </div>
    </div>
  );
};

