              }}>
                {field.label}
              </label>
              {field.type === 'textarea' ? (
                <textarea
                  value={userProfile[field.key as keyof typeof userProfile]}
                  onChange={(e) => handleInputChange(field.key, e.target.value)}
                  disabled={!field.editable}
