import React, { useEffect, useState } from 'react';
import './styles/globals.css';
import { useNotification } from './components/Notification';
import LoadingSpinner from './components/LoadingSpinner';
import GetStarted from './pages/auth/GetStarted';
import PhoneNumberEntry from './pages/auth/PhoneNumberEntry';
import ResponsiveLayout, { MobileContainer, BackButton, Card, FormInput, Button } from './components/ResponsiveLayout';
import DealDetails from './pages/app/DealDetails';
import CommitInvestment from './pages/app/CommitInvestment';



// PhoneNumber Component
interface PageProps {
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
}

const PhoneNumber: React.FC<PageProps> = ({ showNotification }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [invitationCode, setInvitationCode] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (phoneNumber.length !== 10) {
      showNotification('Please enter a valid 10-digit phone number', 'error');
      return;
    }

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/send?phone_number=${phoneNumber}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        localStorage.setItem('phoneNumber', phoneNumber);
        localStorage.setItem('invitationCode', invitationCode);
        if (data.user_id) {
          localStorage.setItem('userId', data.user_id);
        }
        window.history.pushState({}, '', '/verify-phone-otp');
        window.location.reload();
      } else {
        showNotification(data.message || 'Failed to send OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to send OTP. Please check your internet connection.', 'error');
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/get-started');
    window.location.reload();
  };

  const handlePhoneNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '');
    if (value.length <= 10) {
      setPhoneNumber(value);
    }
  };

  return (
    <ResponsiveLayout centered fullHeight backgroundType="gradient">
      <MobileContainer maxWidth="md">
        <BackButton onClick={handleBack} />

        <Card>
          <h1 className="text-responsive-xl font-bold mb-4">
            Enter phone number
          </h1>

          <p className="text-accent mb-6 sm:mb-8 text-sm sm:text-base leading-relaxed">
            Your number helps us verify your identity and keep your account secure.
          </p>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Phone Number Input */}
            <div className="space-y-4">
              <FormInput
                label="Phone Number"
                type="tel"
                value={phoneNumber}
                onChange={handlePhoneNumberChange}
                placeholder="Enter 10-digit number"
                required
                leftIcon={
                  <div className="flex items-center">
                    <span className="text-white text-sm font-medium">+91</span>
                    <div className="w-px h-4 bg-gray-600 mx-2" />
                  </div>
                }
              />

              <FormInput
                label="Invitation Code (Optional)"
                type="text"
                value={invitationCode}
                onChange={(e) => setInvitationCode(e.target.value)}
                placeholder="Enter invitation code"
              />
            </div>

            <Button
              type="submit"
              variant="primary"
              size="lg"
              fullWidth
            >
              Send OTP
            </Button>
          </form>
        </Card>
      </MobileContainer>
    </ResponsiveLayout>
  );
};

// VerifyPhoneOTP Component
const VerifyPhoneOTP: React.FC<PageProps> = ({ showNotification }) => {
  const [otp, setOtp] = useState(['', '', '', '', '', '']);
  const [isResending, setIsResending] = useState(false);

  const handleOTPChange = (index: number, value: string) => {
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      // Auto-focus next input
      if (value && index < 5) {
        const nextInput = document.getElementById(`otp-${index + 1}`) as HTMLInputElement;
        nextInput?.focus();
      }
    }
  };

  const handleKeyDown = (index: number, e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      const prevInput = document.getElementById(`otp-${index - 1}`) as HTMLInputElement;
      prevInput?.focus();
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const otpString = otp.join('');

    if (otpString.length !== 6) {
      showNotification('Please enter the complete 6-digit OTP', 'error');
      return;
    }

    const phoneNumber = localStorage.getItem('phoneNumber');
    const invitationCode = localStorage.getItem('invitationCode') || '';

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/verify?phone_number=${phoneNumber}&otp=${otpString}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        if (data.user_id) {
          localStorage.setItem('userId', data.user_id);
        }
        window.history.pushState({}, '', '/email-entry');
        window.location.reload();
      } else {
        showNotification(data.message || 'Invalid OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to verify OTP. Please check your internet connection.', 'error');
    }
  };

  const handleResendOTP = async () => {
    setIsResending(true);
    const phoneNumber = localStorage.getItem('phoneNumber');
    const invitationCode = localStorage.getItem('invitationCode') || '';

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/send?phone_number=${phoneNumber}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        showNotification('OTP sent successfully!', 'success');
        setOtp(['', '', '', '', '', '']);
      } else {
        showNotification(data.message || 'Failed to resend OTP', 'error');
      }
    } catch (error) {
      showNotification('Failed to resend OTP. Please try again.', 'error');
    } finally {
      setIsResending(false);
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/phone-number');
    window.location.reload();
  };

  const phoneNumber = localStorage.getItem('phoneNumber');

  return (
    <ResponsiveLayout centered fullHeight backgroundType="gradient">
      <MobileContainer maxWidth="md">
        <BackButton onClick={handleBack} />

        <Card>
          <h1 className="text-responsive-xl font-bold mb-4">
            Verify your number
          </h1>

          <p className="text-gray-300 mb-6 sm:mb-8 text-sm sm:text-base">
            We've sent a 6-digit verification code to{' '}
            <span className="text-accent font-medium">+91 {phoneNumber}</span>
          </p>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* OTP Input */}
            <div className="space-y-4">
              <label className="block text-sm font-medium text-gray-300">
                Enter verification code
              </label>
              <div className="flex gap-2 sm:gap-3 justify-center">
                {otp.map((digit, index) => (
                  <input
                    key={index}
                    id={`otp-${index}`}
                    type="text"
                    inputMode="numeric"
                    maxLength={1}
                    value={digit}
                    onChange={(e) => handleOTPChange(index, e.target.value)}
                    onKeyDown={(e) => handleKeyDown(index, e)}
                    className="
                      w-12 h-12 sm:w-14 sm:h-14
                      text-center text-lg sm:text-xl font-bold
                      bg-gray-700 border border-gray-600 rounded-lg
                      text-white placeholder-gray-400
                      focus:outline-none focus:ring-2 focus:ring-accent focus:border-transparent
                      transition-colors duration-200
                    "
                  />
                ))}
              </div>
            </div>

            <div className="space-y-4">
              <Button
                type="submit"
                variant="primary"
                size="lg"
                fullWidth
              >
                Verify OTP
              </Button>

              <Button
                type="button"
                variant="secondary"
                size="md"
                fullWidth
                onClick={handleResendOTP}
                loading={isResending}
              >
                Resend OTP
              </Button>
            </div>
          </form>
        </Card>
      </MobileContainer>
    </ResponsiveLayout>
  );
};

// Placeholder components for other pages - these would be similarly converted
