import React, { useState, useEffect } from 'react';
import Container from '../../components/Container';

interface Deal {
  deal_id: string;
  description: string;
  title: string;
  current_valuation: number;
  round_size: number;
  minimum_investment: number;
  commitment: number;
  instruments: string;
  fund_raised_till_now: number;
  logo_url: string;
  management_fee: number;
  company_stage: string;
  carry: number;
  business_model: string;
}

interface CommitInvestmentProps {
  dealId: string;
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
  onBack: () => void;
  onTermSheet: (dealId: string, investmentAmount: string) => void;
}

const CommitInvestment: React.FC<CommitInvestmentProps> = ({ dealId, showNotification, onBack, onTermSheet }) => {
  const [deal, setDeal] = useState<Deal | null>(null);
  const [loading, setLoading] = useState(true);
  const [investmentAmount, setInvestmentAmount] = useState('');
  const [parsedAmount, setParsedAmount] = useState(0);
  const [checked, setChecked] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);

  useEffect(() => {
    const fetchDealDetails = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://api.fundos.services/api/v1/live/deals/?deal_id=${dealId}`);

        if (!response.ok) {
          throw new Error('Failed to fetch deal details');
        }

        const data = await response.json();
        setDeal(data);
      } catch (error) {
        console.error('Error fetching deal details:', error);
        showNotification('Failed to load deal details. Please try again.', 'error');
      } finally {
        setLoading(false);
      }
    };

    if (dealId) {
      fetchDealDetails();
    }
  }, [dealId, showNotification]);

  useEffect(() => {
    const cleanAmount = investmentAmount.replace(/[^0-9.]/g, '');
    const num = parseFloat(cleanAmount);
    setParsedAmount(isNaN(num) ? 0 : num);
  }, [investmentAmount]);

  const formatCurrency = (value: number) => {
    if (!value || isNaN(value)) {
      return '₹ 0';
    }
    return `₹ ${value.toLocaleString('en-IN', {
      maximumFractionDigits: 0,
    })}`;
  };

  const handleInvestmentAmountChange = (text: string) => {
    const cleanText = text.replace(/[^0-9.]/g, '');
    const parts = cleanText.split('.');
    if (parts.length > 2) {
      return;
    }
    setInvestmentAmount(cleanText);
  };

  const handleInputChange = () => {
    const value = parseFloat(investmentAmount) || 0;
    const minValue = deal?.minimum_investment || 0;

    if (value < minValue) {
      showNotification(`Minimum Investment Amount: ${formatCurrency(minValue)}`, 'error');
    } else {
      setModalVisible(true);
    }
  };

  const handleContinue = () => {
    if (!checked) {
      showNotification('Please check the acknowledgment to continue', 'error');
      return;
    }

    try {
      onTermSheet(dealId, parsedAmount.toString());
    } catch (error) {
      console.error('Navigation error:', error);
      showNotification('Navigation failed. Please try again.', 'error');
    }
  };

  const handleModalClose = () => {
    setModalVisible(false);
  };

  const toggleCheckbox = () => {
    setChecked(!checked);
  };

  const managementFeeRate = deal?.management_fee || 0;
  const managementFee = parsedAmount * (managementFeeRate / 100);
  const gst = managementFee * 0.18;
  const total = parsedAmount + managementFee + gst;

  const isInvestmentValid = parsedAmount > 0 && parsedAmount >= (deal?.minimum_investment || 0);

  if (loading) {
    return (
      <Container>
        <div className="p-4">
          <button 
            onClick={onBack}
            className="text-gray-400 text-2xl mb-6 hover:text-white transition-colors"
          >
            ←
          </button>

          <div className="flex items-center justify-center">
            <div className="w-full max-w-[400px] bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-8 backdrop-blur-lg text-center">
              <div className="w-[60px] h-[60px] border-4 border-gray-700 border-t-accent rounded-full animate-spin mx-auto mb-5"></div>
              <h2 className="text-2xl font-medium text-white mb-2">Loading Deal Details</h2>
              <p className="text-sm text-gray-400">Please wait while we load your investment details...</p>
            </div>
          </div>
        </div>
      </Container>
    );
  }

  if (!deal) {
    return (
      <Container>
        <div className="p-4">
          <button 
            onClick={onBack}
            className="text-gray-400 text-2xl mb-6 hover:text-white transition-colors"
          >
            ←
          </button>

          <div className="flex items-center justify-center">
            <div className="w-full max-w-[400px] bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-8 backdrop-blur-lg text-center">
              <h2 className="text-2xl font-medium text-white mb-2">Deal Not Found</h2>
              <p className="text-sm text-gray-400 mb-6">We couldn't find the investment details you're looking for.</p>
              <button
                onClick={onBack}
                className="w-full bg-accent text-primary font-medium py-3 px-6 rounded-lg hover:bg-opacity-90 transition-colors"
              >
                Go Back
              </button>
            </div>
          </div>
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <div className="p-4">
        <button 
          onClick={onBack}
          className="text-gray-400 text-2xl mb-6 hover:text-white transition-colors"
        >
          ←
        </button>

        <div className="space-y-6">
          <div className="bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-6 backdrop-blur-lg">
            <h2 className="text-xl font-bold text-white mb-4">Investment Amount</h2>
            <div className="space-y-2">
              <label className="block text-sm text-gray-400">Enter Amount (INR)</label>
              <input
                type="text"
                value={investmentAmount}
                onChange={(e) => handleInvestmentAmountChange(e.target.value)}
                placeholder="Enter amount"
                className="w-full bg-opacity-10 bg-white border border-white border-opacity-20 rounded-lg p-3 text-white placeholder-gray-500 focus:outline-none focus:border-accent"
              />
              <p className="text-sm text-gray-400">
                Minimum Investment: {formatCurrency(deal.minimum_investment)}
              </p>
            </div>
          </div>

          {parsedAmount > 0 && (
            <div className="bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-6 backdrop-blur-lg">
              <h3 className="text-lg font-semibold text-white mb-4">Fee Breakdown</h3>
              <div className="space-y-3">
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">Investment Amount</span>
                  <span className="text-white">{formatCurrency(parsedAmount)}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">Management Fee ({managementFeeRate}%)</span>
                  <span className="text-white">{formatCurrency(managementFee)}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">GST (18%)</span>
                  <span className="text-white">{formatCurrency(gst)}</span>
                </div>
                <div className="h-px bg-white bg-opacity-10 my-2"></div>
                <div className="flex justify-between font-medium">
                  <span className="text-white">Total</span>
                  <span className="text-white">{formatCurrency(total)}</span>
                </div>
              </div>
            </div>
          )}

          <div className="flex items-start gap-3 p-4 bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl">
            <input
              type="checkbox"
              checked={checked}
              onChange={toggleCheckbox}
              className="mt-1"
            />
            <p className="text-sm text-gray-400">
              I acknowledge that this investment is subject to the terms and conditions outlined in the investment agreement.
            </p>
          </div>

          <button
            onClick={handleInputChange}
            disabled={!isInvestmentValid || !checked}
            className={`w-full py-3 px-6 rounded-lg font-medium transition-colors ${
              isInvestmentValid && checked
                ? 'bg-accent text-primary hover:bg-opacity-90'
                : 'bg-gray-700 text-gray-400 cursor-not-allowed'
            }`}
          >
            Continue to Term Sheet
          </button>
        </div>

        {modalVisible && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
            <div className="bg-[#1a1a1a] rounded-xl p-6 max-w-md w-full">
              <h3 className="text-xl font-bold text-white mb-4">Confirm Investment</h3>
              <p className="text-gray-400 mb-6">
                You are about to invest {formatCurrency(parsedAmount)} in {deal.title}. This action cannot be undone.
              </p>
              <div className="flex gap-4">
                <button
                  onClick={handleModalClose}
                  className="flex-1 bg-opacity-10 bg-white text-white font-medium py-3 px-6 rounded-lg hover:bg-opacity-20 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleContinue}
                  className="flex-1 bg-accent text-primary font-medium py-3 px-6 rounded-lg hover:bg-opacity-90 transition-colors"
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Container>
  );
};

export default CommitInvestment;
