  return (
    <div className={`w-full ${fullHeight ? 'min-h-screen' : ''} bg-[#1a1a1a]`}>
      <div className="mx-auto w-full max-w-[480px] relative bg-[#1a1a1a] min-h-screen shadow-2xl md:shadow-[0_0_50px_rgba(0,0,0,0.3)] md:border-x md:border-white/[0.05]">
        {children}
      </div>
    </div>
  );
};

export default Container;
