import React, { useState } from 'react';
import Container from '../../components/Container';

interface EmailScreenProps {
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
}

const EmailScreen: React.FC<EmailScreenProps> = ({ showNotification }) => {
  const [email, setEmail] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email) {
      showNotification('Please enter your email address', 'error');
      return;
    }

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/email/otp/send?email=${encodeURIComponent(email)}`
      );
      const data = await response.json();

      if (data.success) {
        localStorage.setItem('email', email);
        window.history.pushState({}, '', '/verify-email-otp');
        window.location.reload();
      } else {
        showNotification(data.message || 'Failed to send OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to send OTP. Please check your internet connection.', 'error');
    }
  };

  const handleBack = () => {
    window.history.pushState({}, '', '/verify-phone-otp');
    window.location.reload();
  };

  return (
    <Container>
      <div className="min-h-screen flex flex-col md:justify-center p-4">
        <button 
          onClick={handleBack}
          className="text-gray-400 text-2xl mb-6 hover:text-white transition-colors md:hidden"
        >
          ‚Üê
        </button>

        <div className="space-y-6 md:text-center">
          <div>
            <h1 className="text-3xl font-bold text-white mb-2">
              Enter your email
            </h1>
            <p className="text-accent text-sm">
              We'll send you a verification code to confirm your email address.
            </p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6 max-w-sm mx-auto">
            <div>
              <label className="block text-gray-200 text-sm mb-2">
                Email Address
              </label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email address"
                className="w-full bg-opacity-10 bg-white border border-white border-opacity-20 rounded-lg p-3 text-white placeholder-gray-500 focus:outline-none focus:border-accent"
              />
            </div>

            <button
              type="submit"
              className="w-full bg-accent text-primary font-medium py-3 px-6 rounded-lg hover:bg-opacity-90 transition-colors"
            >
              Continue
            </button>
          </form>
        </div>
      </div>
    </Container>
  );
};

export default EmailScreen;
