import React, { useState, useEffect } from 'react';
import PageWrapper from "../../components/PageWrapper";

interface Deal {
  deal_id: string;
  description: string;
  title: string;
  current_valuation: number;
  round_size: number;
  minimum_investment: number;
  commitment: number;
  instruments: string;
  fund_raised_till_now: number;
  logo_url: string;
  management_fee: number;
  company_stage: string;
  carry: number;
  business_model: string;
}

interface DealDetailsProps {
  dealId: string;
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
  onBack: () => void;
  onCommit: (dealId: string) => void;
}

const DealDetails: React.FC<DealDetailsProps> = ({ dealId, showNotification, onBack, onCommit }) => {
  const [deal, setDeal] = useState<Deal | null>(null);
  const [loading, setLoading] = useState(true);
  const [isDescriptionExpanded, setIsDescriptionExpanded] = useState(false);

  useEffect(() => {
    const fetchDealDetails = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://api.fundos.services/api/v1/live/deals/?deal_id=${dealId}`);

        if (!response.ok) {
          throw new Error('Failed to fetch deal details');
        }

        const data = await response.json();
        setDeal(data);
      } catch (error) {
        console.error('Error fetching deal details:', error);
        showNotification('Failed to load deal details. Please try again.', 'error');
      } finally {
        setLoading(false);
      }
    };

    if (dealId) {
      fetchDealDetails();
    }
  }, [dealId, showNotification]);

  const convertToCrores = (value: number) => {
    return `${(value / 10000000).toFixed(2)} Cr`;
  };

  const handleCommit = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        showNotification('User not authenticated', 'error');
        return;
      }

      // Check MCA status first
      const mcaResponse = await fetch(`https://api.fundos.services/api/v1/live/deals/mca/status?user_id=${userId}`);

      if (!mcaResponse.ok) {
        throw new Error('Failed to check MCA status');
      }

      const mcaData = await mcaResponse.json();

      if (mcaData?.message) {
        showNotification(mcaData.message, 'success');
      }

      if (mcaData.sent_status === 'success' && mcaData.request_status === 'completed') {
        onCommit(dealId);
      } else if (mcaData.sent_status === 'success' && mcaData.request_status === 'inprogress') {
        showNotification('Signing Docs in progress. Please contact admin.', 'error');
      } else {
        showNotification('Ask your fund manager to sign the docs!', 'error');
      }
    } catch (error) {
      console.error('Error in commit process:', error);
      showNotification('Failed to apply for declarations. Please try again.', 'error');
    }
  };

  const handleNotInterested = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        showNotification('User not authenticated', 'error');
        return;
      }

      const response = await fetch(`https://api.fundos.services/api/v1/live/deals/interaction?deal_id=${dealId}&user_id=${userId}&not_interested=true`, {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error('Failed to set not interested');
      }

      const data = await response.json();

      if (data?.message) {
        showNotification(data.message, 'success');
      }

      onBack(); // Go back to dashboard
    } catch (error) {
      console.error('Error setting not interested:', error);
      showNotification('Failed to set not interested. Please try again.', 'error');
    }
  };

  const toggleDescription = () => {
    setIsDescriptionExpanded(!isDescriptionExpanded);
  };

  if (loading) {
    return (
      <PageWrapper>
        <div className="flex flex-col gap-6">
          <h1 className="text-2xl font-bold">Deal Details</h1>

          {/* Deal summary card */}
          <div className="bg-gray-700 rounded-lg p-6">
            <div style={{
              width: '100%', 
              maxWidth: '400px',
              margin: '0 auto'
            }}>
              <div style={{
                width: '60px',
                height: '60px',
                border: '4px solid #374151',
                borderTop: '4px solid #00fb57',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
                margin: '0 auto 20px'
              }}></div>
              <h2 style={{
                fontSize: '1.5rem',
                fontWeight: '500',
                color: '#FDFDFD',
                margin: '0 0 10px 0'
              }}>
                Loading Deal Details
              </h2>
              <p style={{
                fontSize: '14px',
                color: '#9ca3af',
                margin: 0
              }}>
                Please wait while we fetch the deal information...
              </p>
            </div>
          </div>
        </div>
      </PageWrapper>
    );
  }

  if (!deal) {
    return (
      <PageWrapper>
        <div className="flex flex-col gap-6">
          <h1 className="text-2xl font-bold">Deal Details</h1>

          {/* Deal summary card */}
          <div className="bg-gray-700 rounded-lg p-6">
            <div style={{
              width: '100%', 
              maxWidth: '400px',
              margin: '0 auto'
            }}>
              <h2 style={{
                fontSize: '1.5rem',
                fontWeight: '500',
                color: '#FDFDFD',
                margin: '0 0 20px 0'
              }}>
                Deal Not Found
              </h2>
              <button
                onClick={onBack}
                style={{
                  background: '#00fb57',
                  color: '#1a1a1a',
                  border: 'none',
                  padding: '12px 24px',
                  fontSize: '14px',
                  fontWeight: '600',
                  borderRadius: '6px',
                  cursor: 'pointer'
                }}
              >
                Back to Dashboard
              </button>
            </div>
          </div>
        </div>
      </PageWrapper>
    );
  }

  return (
    <PageWrapper>
      <div className="flex flex-col gap-6">
        <h1 className="text-2xl font-bold">Deal Details</h1>

        {/* Deal summary card */}
        <div className="bg-gray-700 rounded-lg p-6">
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            <div>
              <h3 className="text-sm text-gray-400">Deal Name</h3>
              <p className="text-lg font-semibold">{deal.title}</p>
            </div>
            <div>
              <h3 className="text-sm text-gray-400">Investment Amount</h3>
              <p className="text-lg font-semibold">₹{convertToCrores(deal.round_size)}</p>
            </div>
            <div>
              <h3 className="text-sm text-gray-400">Status</h3>
              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-accent text-primary">
                Active
              </span>
            </div>
          </div>
        </div>

        {/* Deal details sections */}
        <div className="grid gap-6 md:grid-cols-2">
          {/* Company Information */}
          <div className="bg-gray-700 rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Company Information</h2>
            <div className="space-y-4">
              <div>
                <h3 className="text-sm text-gray-400">Company Name</h3>
                <p className="text-base">{deal.title}</p>
              </div>
              <div>
                <h3 className="text-sm text-gray-400">Industry</h3>
                <p className="text-base">{deal.business_model?.toUpperCase() || 'N/A'}</p>
              </div>
              <div>
                <h3 className="text-sm text-gray-400">Stage</h3>
                <p className="text-base">{deal.company_stage?.toUpperCase() || 'N/A'}</p>
              </div>
            </div>
          </div>

          {/* Investment Terms */}
          <div className="bg-gray-700 rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Investment Terms</h2>
            <div className="space-y-4">
              <div>
                <h3 className="text-sm text-gray-400">Valuation</h3>
                <p className="text-base">₹{convertToCrores(deal.current_valuation)}</p>
              </div>
              <div>
                <h3 className="text-sm text-gray-400">Equity Percentage</h3>
                <p className="text-base">{deal.carry}%</p>
              </div>
              <div>
                <h3 className="text-sm text-gray-400">Lock-in Period</h3>
                <p className="text-base">{deal.commitment} years</p>
              </div>
            </div>
          </div>
        </div>

        {/* Description */}
        <div className="bg-gray-700 rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Description</h2>
          <p className="text-base">
            {deal.description}
          </p>
        </div>

        {/* Deal Details Card */}
        <div className="bg-gray-700 rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">📊 Deal Information</h2>

          {/* Current Valuation - Featured */}
          <div className="mb-4">
            <p className="text-sm text-gray-400 mb-2">CURRENT VALUATION</p>
            <p className="text-lg font-semibold">
              INR {convertToCrores(deal.current_valuation)}{' '}
              <span className="text-sm text-gray-400 font-normal">
                (Post)
              </span>
            </p>
          </div>

          {/* Two Column Layout */}
          <div className="grid gap-4 md:grid-cols-2">
            <div>
              <p className="text-sm text-gray-400 mb-2">STAGE</p>
              <p className="text-base font-semibold">{deal.company_stage?.toUpperCase() || 'N/A'}</p>
            </div>
            <div>
              <p className="text-sm text-gray-400 mb-2">INSTRUMENT</p>
              <p className="text-base font-semibold">{deal.instruments?.toUpperCase() || 'N/A'}</p>
            </div>
          </div>

          <div className="grid gap-4 md:grid-cols-2">
            <div>
              <p className="text-sm text-gray-400 mb-2">ROUND SIZE</p>
              <p className="text-base font-semibold">INR {convertToCrores(deal.round_size)}</p>
            </div>
            <div>
              <p className="text-sm text-gray-400 mb-2">MIN. INVESTMENT</p>
              <p className="text-base font-semibold">INR {convertToCrores(deal.minimum_investment)}</p>
            </div>
          </div>

          <div className="grid gap-4 md:grid-cols-2">
            <div>
              <p className="text-sm text-gray-400 mb-2">COMMITMENTS</p>
              <p className="text-base font-semibold">INR {convertToCrores(deal.commitment)}</p>
            </div>
            <div>
              <p className="text-sm text-gray-400 mb-2">VALUATION</p>
              <p className="text-base font-semibold">INR {convertToCrores(2500000)}</p>
            </div>
          </div>

          {/* Progress Section */}
          <div className="mt-4">
            <p className="text-sm text-gray-400 mb-2">📈 {deal.fund_raised_till_now}% funds raised till now</p>
            <div className="h-2 bg-gray-600 rounded-full overflow-hidden">
              <div className="h-2 bg-accent" style={{ width: `${deal.fund_raised_till_now}%` }}></div>
            </div>
          </div>
        </div>

        {/* Action buttons */}
        <div className="flex flex-col md:flex-row gap-4 mt-4">
          <button
            onClick={handleCommit}
            className="
              w-full
              md:w-auto
              bg-accent
              text-primary
              font-semibold
              py-3
              px-6
              rounded-lg
              hover:bg-opacity-90
              transition-colors
            "
          >
            💰 Commit Investment
          </button>
          <button
            onClick={handleNotInterested}
            className="
              w-full
              md:w-auto
              border
              border-accent
              text-accent
              font-semibold
              py-3
              px-6
              rounded-lg
              hover:bg-accent
              hover:text-primary
              transition-colors
            "
          >
            Not Interested
          </button>
        </div>
      </div>
    </PageWrapper>
  );
};

export default DealDetails;
