import React, { ReactNode } from 'react';

interface ResponsiveLayoutProps {
  children: ReactNode;
  className?: string;
  fullHeight?: boolean;
  centered?: boolean;
  padding?: 'none' | 'sm' | 'md' | 'lg';
  backgroundType?: 'solid' | 'gradient';
}

const ResponsiveLayout: React.FC<ResponsiveLayoutProps> = ({
  children,
  className = '',
  fullHeight = false,
  centered = false,
  padding = 'md',
  backgroundType = 'solid'
}) => {
  const paddingClasses = {
    none: '',
    sm: 'p-2 sm:p-4',
    md: 'p-4 sm:p-6',
    lg: 'p-6 sm:p-8 md:p-12',
  };

  const backgroundClasses = {
    solid: 'bg-gray-50',
    gradient: 'bg-gradient-to-br from-gray-50 to-gray-100',
  };

  const baseClasses = `
    mobile-container
    ${fullHeight ? 'min-h-screen-safe' : ''}
    ${centered ? 'flex items-center justify-center' : ''}
    ${backgroundClasses[backgroundType]}
    ${paddingClasses[padding]}
    ${className}
  `.trim().replace(/\s+/g, ' ');

  return (
    <>
      {/* iOS Status Bar Background */}
      <div className="ios-status-bar-bg md:hidden" />

      {/* Main Content */}
      <div className={baseClasses}>
        {children}
      </div>
    </>
  );
};

// Mobile-first container for content sections
interface MobileContainerProps {
  children: ReactNode;
  className?: string;
  maxWidth?: 'sm' | 'md' | 'lg' | 'full';
}

export const MobileContainer: React.FC<MobileContainerProps> = ({
  children,
  className = '',
  maxWidth = 'md'
}) => {
  const maxWidthClasses = {
    sm: 'max-w-sm',
    md: 'max-w-md',
    lg: 'max-w-lg',
    full: 'max-w-none w-full',
  };

  return (
    <div className={`w-full ${maxWidthClasses[maxWidth]} ${className}`}>
      {children}
    </div>
  );
};

// Back button component
interface BackButtonProps {
  onClick: () => void;
  className?: string;
}

export const BackButton: React.FC<BackButtonProps> = ({ onClick, className = '' }) => {
  return (
    <button
      onClick={onClick}
      className={`
        touch-target 
        mb-4 
        text-gray-400 
        hover:text-white 
        transition-colors 
        duration-200
        focus-visible
        ${className}
      `}
      aria-label="Go back"
    >
      <svg 
        className="w-6 h-6" 
        fill="none" 
        stroke="currentColor" 
        viewBox="0 0 24 24"
      >
        <path 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          strokeWidth={2} 
          d="M15 19l-7-7 7-7" 
        />
      </svg>
    </button>
  );
};

// Card component for form sections
interface CardProps {
  children: ReactNode;
  className?: string;
}

export const Card: React.FC<CardProps> = ({ children, className = '' }) => {
  return (
    <div className={`card-base p-6 sm:p-8 ${className}`}>
      {children}
    </div>
  );
};

// Form input component
interface FormInputProps {
  label: string;
  type?: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  placeholder?: string;
  required?: boolean;
  disabled?: boolean;
  leftIcon?: ReactNode;
  rightIcon?: ReactNode;
  className?: string;
}

export const FormInput: React.FC<FormInputProps> = ({
  label,
  type = 'text',
  value,
  onChange,
  placeholder,
  required = false,
  disabled = false,
  leftIcon,
  rightIcon,
  className = '',
}) => {
  return (
    <div className={`space-y-2 ${className}`}>
      <label className="block text-sm font-medium text-gray-700">
        {label}
        {required && <span className="text-red-500 ml-1">*</span>}
      </label>
      <div className="relative">
        {leftIcon && (
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            {leftIcon}
          </div>
        )}
        <input
          type={type}
          value={value}
          onChange={onChange}
          placeholder={placeholder}
          required={required}
          disabled={disabled}
          className={`
            input-base
            ${leftIcon ? 'pl-12' : ''}
            ${rightIcon ? 'pr-12' : ''}
            ${disabled ? 'opacity-50 cursor-not-allowed' : ''}
          `}
        />
        {rightIcon && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            {rightIcon}
          </div>
        )}
      </div>
    </div>
  );
};

// Primary button component
interface ButtonProps {
  children: ReactNode;
  onClick?: () => void;
  type?: 'button' | 'submit' | 'reset';
  variant?: 'primary' | 'secondary' | 'accent';
  size?: 'sm' | 'md' | 'lg';
  fullWidth?: boolean;
  loading?: boolean;
  disabled?: boolean;
  className?: string;
}

export const Button: React.FC<ButtonProps> = ({
  children,
  onClick,
  type = 'button',
  variant = 'primary',
  size = 'md',
  fullWidth = false,
  loading = false,
  disabled = false,
  className = '',
}) => {
  const baseClasses = 'btn-base transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2';

  const variantClasses = {
    primary: 'bg-white text-gray-900 border border-gray-300 hover:bg-gray-50 active:bg-gray-100 focus:ring-gray-500',
    secondary: 'bg-transparent border border-gray-300 text-gray-700 hover:bg-gray-50 active:bg-gray-100 focus:ring-gray-500',
    accent: 'bg-green-500 text-white hover:bg-green-600 active:bg-green-700 focus:ring-green-500',
  };

  const sizeClasses = {
    sm: 'px-4 py-2 text-sm font-medium rounded-md min-h-[40px]',
    md: 'px-6 py-3 text-base font-medium rounded-lg min-h-[48px]',
    lg: 'px-8 py-4 text-lg font-medium rounded-lg min-h-[56px]',
  };

  const classes = `
    ${baseClasses}
    ${variantClasses[variant]}
    ${sizeClasses[size]}
    ${fullWidth ? 'w-full' : ''}
    ${disabled || loading ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}
    ${className}
  `;

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled || loading}
      className={classes}
    >
      {loading ? (
        <div className="flex items-center justify-center">
          <div className="loading-spinner w-5 h-5 mr-2" />
          Loading...
        </div>
      ) : (
        children
      )}
    </button>
  );
};

export default ResponsiveLayout;
