// Simplified Dashboard - this would need full responsive implementation
interface Deal {
  deal_id: string;
  description: string;
  title: string;
  current_valuation: number;
  round_size: number;
  commitment: number;
  logo_url: string;
  minimum_investment: number;
  deal_status: 'open' | 'closed' | 'on_hold';
}

const Dashboard: React.FC<PageProps> = ({ showNotification }) => {
  const [deals, setDeals] = useState<Deal[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('investments');

  useEffect(() => {
    const fetchDeals = async () => {
      try {
        const userId = localStorage.getItem('userId');
        const response = await fetch(`https://api.fundos.services/api/v0/test/user/deals?user_id=${userId}`);
        const data = await response.json();

        if (data.success && data.deals) {
          setDeals(data.deals);
        }
      } catch (error) {
        console.error('Error fetching deals:', error);
        showNotification('Failed to load deals', 'error');
      } finally {
        setLoading(false);
      }
    };

    fetchDeals();
  }, [showNotification]);

  const handleLogout = () => {
    localStorage.clear();
    window.history.pushState({}, '', '/get-started');
    window.location.reload();
  };

  const handleViewDeal = (dealId: string) => {
    window.history.pushState({}, '', `/deal-details?dealId=${dealId}`);
    window.location.reload();
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <ResponsiveLayout fullHeight padding="none" backgroundType="solid">
      <div className="flex flex-col min-h-dvh">
        {/* Header */}
        <div className="bg-primary border-b border-gray-800 px-4 py-4 sm:px-6">
          <div className="flex items-center justify-between">
            <h1 className="text-responsive-lg font-bold text-white">FundOS</h1>
            <Button
              onClick={handleLogout}
              variant="secondary"
              size="sm"
            >
              Logout
            </Button>
          </div>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto ios-scroll-fix p-4 sm:p-6">
          <div className="space-y-6">
            <div>
              <h2 className="text-responsive-base font-semibold text-white mb-4">
                Available Investments
              </h2>

              {deals.length === 0 ? (
                <Card className="text-center py-8">
                  <p className="text-gray-400 mb-4">No deals available at the moment</p>
                  <p className="text-sm text-gray-500">Check back later for new investment opportunities</p>
                </Card>
              ) : (
                <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
                  {deals.map((deal) => (
                    <Card key={deal.deal_id} className="cursor-pointer hover:bg-gray-700/50 transition-colors">
                      <div onClick={() => handleViewDeal(deal.deal_id)}>
                        <div className="flex items-start gap-4 mb-4">
                          {deal.logo_url && (
                            <img
                              src={deal.logo_url}
                              alt={deal.title}
                              className="w-12 h-12 rounded-lg object-cover"
                            />
                          )}
                          <div className="flex-1 min-w-0">
                            <h3 className="font-semibold text-white truncate">{deal.title}</h3>
                            <p className="text-sm text-gray-400 mt-1 line-clamp-2">{deal.description}</p>
                          </div>
                        </div>

                        <div className="space-y-2 text-sm">
                          <div className="flex justify-between">
                            <span className="text-gray-400">Valuation:</span>
                            <span className="text-white font-medium">₹{deal.current_valuation.toLocaleString()}</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-gray-400">Min Investment:</span>
                            <span className="text-white font-medium">₹{deal.minimum_investment.toLocaleString()}</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-gray-400">Status:</span>
                            <span className={`font-medium ${
                              deal.deal_status === 'open' ? 'text-accent' : 
                              deal.deal_status === 'closed' ? 'text-error' : 'text-warning'
                            }`}>
                              {deal.deal_status.charAt(0).toUpperCase() + deal.deal_status.slice(1)}
                            </span>
                          </div>
                        </div>
                      </div>
                    </Card>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Bottom Navigation */}
        <div className="bg-primary border-t border-gray-800 px-4 py-2 sm:px-6">
          <div className="flex justify-around">
            {[
              { id: 'investments', title: 'Investments', icon: '💼' },
              { id: 'portfolio', title: 'Portfolio', icon: '📊' },
              { id: 'profile', title: 'Profile', icon: '👤' }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`
                  touch-target flex flex-col items-center justify-center gap-1
                  transition-colors duration-200
                  ${activeTab === tab.id ? 'text-accent' : 'text-gray-400'}
                `}
              >
                <span className="text-lg">{tab.icon}</span>
                <span className="text-xs font-medium">{tab.title}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
    </ResponsiveLayout>
  );
};

const App = () => {
  const [currentRoute, setCurrentRoute] = useState('/');
  const [isLoading, setIsLoading] = useState(true);
  const { showNotification, NotificationContainer } = useNotification();

  useEffect(() => {
    const path = window.location.pathname;
    setCurrentRoute(path);

    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1000);

    const handlePopState = () => {
      setCurrentRoute(window.location.pathname);
    };

    window.addEventListener('popstate', handlePopState);

    return () => {
      clearTimeout(timer);
      window.removeEventListener('popstate', handlePopState);
    };
  }, []);

  if (isLoading) {
    return <LoadingSpinner />;
  }

  const renderPage = () => {
    const pageProps = { showNotification };

    // Extract query parameters
    const urlParams = new URLSearchParams(window.location.search);
    const dealId = urlParams.get('dealId') || '';
    const investmentAmount = urlParams.get('investmentAmount') || '';

    const handleBackToDashboard = () => {
      window.history.pushState({}, '', '/dashboard');
      window.location.reload();
    };

    const handleCommitInvestment = (dealId: string) => {
      window.history.pushState({}, '', `/commit-investment?dealId=${dealId}`);
      window.location.reload();
    };

    const handleTermSheet = (dealId: string, investmentAmount: string) => {
      window.history.pushState({}, '', `/term-sheet?dealId=${dealId}&investmentAmount=${investmentAmount}`);
      window.location.reload();
    };

    const handleBackToDeals = () => {
      const urlParams = new URLSearchParams(window.location.search);
      const dealId = urlParams.get('dealId');
      if (dealId) {
        window.history.pushState({}, '', `/deal-details?dealId=${dealId}`);
        window.location.reload();
      } else {
        handleBackToDashboard();
      }
    };

    switch (currentRoute.split('?')[0]) {
      case '/phone-number':
        return <PhoneNumberEntry {...pageProps} />;
      case '/verify-phone-otp':
        return <VerifyPhoneOTPPage {...pageProps} />;
      case '/email-entry':
        return <EmailEntry {...pageProps} />;
      case '/verify-email-otp':
        return <VerifyEmailOTP {...pageProps} />;
      case '/choose-investor':
        return <ChooseInvestor {...pageProps} />;
      case '/kyc-start':
        return <KYCStart {...pageProps} />;
      case '/aadhaar-verification':
        return <AadhaarVerification {...pageProps} />;
      case '/pan-verification':
        return <PANVerification {...pageProps} />;
      case '/bank-details':
        return <BankDetails {...pageProps} />;
      case '/professional-background':
        return <ProfessionalBackground {...pageProps} />;
      case '/user-details':
        return <UserDetails {...pageProps} />;
      case '/contribution-agreement':
        return <ContributionAgreement {...pageProps} />;
      case '/upload-photo':
        return <UploadPhoto {...pageProps} />;
      case '/final-approval':
        return <FinalApproval {...pageProps} />;
      case '/dashboard':
        return <Dashboard {...pageProps} />;
      case '/deal-details':
        return (
          <DealDetails 
            dealId={dealId}
            showNotification={showNotification}
            onBack={handleBackToDashboard}
            onCommit={handleCommitInvestment}
          />
        );
      case '/commit-investment':
        return (
          <CommitInvestment
            dealId={dealId}
            showNotification={showNotification}
            onBack={handleBackToDeals}
            onTermSheet={handleTermSheet}
          />
        );
      case '/term-sheet':
        return (
          <ResponsiveLayout centered fullHeight>
            <MobileContainer>
              <Card className="text-center">
                <h1 className="text-2xl font-bold mb-4">Term Sheet</h1>
                <div className="space-y-2 mb-6 text-gray-300">
                  <p>Deal ID: {dealId}</p>
                  <p>Investment Amount: ₹{investmentAmount}</p>
                </div>
                <p className="text-gray-400 text-sm mb-6">
                  Term sheet functionality coming soon!
                </p>
                <Button
                  onClick={handleBackToDeals}
                  variant="accent"
                  size="md"
                  fullWidth
                >
                  Back to Deal Details
                </Button>
              </Card>
            </MobileContainer>
          </ResponsiveLayout>
        );
      case '/':
      case '/get-started':
      default:
        return <GetStarted />;
    }
  };

  return (
    <>
      {renderPage()}
      <NotificationContainer />
    </>
  );
};

export default App;
