import React from 'react';
import { useMediaQuery } from 'react-responsive';

interface PageWrapperProps {
  children: React.ReactNode;
  hideBackOnDesktop?: boolean;
}

const PageWrapper: React.FC<PageWrapperProps> = ({ 
  children, 
  hideBackOnDesktop = true 
}) => {
  const isTabletOrDesktop = useMediaQuery({ minWidth: 768 });

  // Debug information
  console.log('Is tablet or desktop:', isTabletOrDesktop);

  return (
    <div 
      className="min-h-screen w-full"
      style={{ 
        backgroundColor: '#1a1a1a',
        color: 'white',
      }}
    >
      {/* Desktop/Tablet Container */}
      <div 
        className="w-full min-h-screen"
        style={{
          background: isTabletOrDesktop ? 'linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)' : 'none',
          padding: isTabletOrDesktop ? '2rem' : '0',
        }}
      >
        {/* Content Container */}
        <div
          style={{
            width: '100%',
            maxWidth: isTabletOrDesktop ? '768px' : '100%',
            margin: isTabletOrDesktop ? '0 auto' : '0',
            minHeight: '100vh',
            position: 'relative',
          }}
        >
          {/* Content Wrapper */}
          <div
            style={{
              width: '100%',
              height: '100%',
              backgroundColor: isTabletOrDesktop ? '#374151' : '#1a1a1a',
              borderRadius: isTabletOrDesktop ? '1rem' : '0',
              overflow: 'hidden',
              boxShadow: isTabletOrDesktop ? '0 4px 6px rgba(0, 0, 0, 0.1)' : 'none',
            }}
          >
            {/* Back Button Container */}
            {(!isTabletOrDesktop || !hideBackOnDesktop) && (
              <div style={{ padding: '1rem' }}>
                {/* Back button placeholder */}
              </div>
            )}

            {/* Main Content Area */}
            <div
              style={{
                padding: isTabletOrDesktop ? '2rem' : '1rem',
                maxHeight: isTabletOrDesktop ? 'calc(100vh - 4rem)' : '100%',
                overflowY: 'auto',
              }}
            >
              {children}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PageWrapper;
