import React, { useState } from 'react';
import ResponsiveLayout, { MobileContainer, Card, Button } from '../../components/ResponsiveLayout';

interface PageProps {
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
}

const VerifyPhoneOTP: React.FC<PageProps> = ({ showNotification }) => {
  const [otp, setOtp] = useState(['', '', '', '']);
  const [isResending, setIsResending] = useState(false);

  const handleOTPChange = (index: number, value: string) => {
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      // Auto-focus next input
      if (value && index < 3) {
        const nextInput = document.getElementById(`otp-${index + 1}`) as HTMLInputElement;
        nextInput?.focus();
      }
    }
  };

  const handleKeyDown = (index: number, e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      const prevInput = document.getElementById(`otp-${index - 1}`) as HTMLInputElement;
      prevInput?.focus();
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const otpString = otp.join('');

    if (otpString.length !== 4) {
      showNotification('Please enter the complete 4-digit OTP', 'error');
      return;
    }

    const phoneNumber = localStorage.getItem('phoneNumber');
    const invitationCode = localStorage.getItem('invitationCode') || '';

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/verify?phone_number=${phoneNumber}&otp=${otpString}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        if (data.user_id) {
          localStorage.setItem('userId', data.user_id);
        }
        window.history.pushState({}, '', '/email-entry');
        window.location.reload();
      } else {
        showNotification(data.message || 'Invalid OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to verify OTP. Please check your internet connection.', 'error');
    }
  };

  const handleResendOTP = async () => {
    setIsResending(true);
    const phoneNumber = localStorage.getItem('phoneNumber');
    const invitationCode = localStorage.getItem('invitationCode') || '';

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/send?phone_number=${phoneNumber}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        showNotification('OTP sent successfully!', 'success');
        setOtp(['', '', '', '']);
      } else {
        showNotification(data.message || 'Failed to resend OTP', 'error');
      }
    } catch (error) {
      showNotification('Failed to resend OTP. Please try again.', 'error');
    } finally {
      setIsResending(false);
    }
  };

  const phoneNumber = localStorage.getItem('phoneNumber');

  return (
    <ResponsiveLayout fullHeight padding="none" backgroundType="solid">
      <div className="full-width-mobile flex items-center justify-center bg-gray-50">
        <MobileContainer maxWidth="full" className="w-full px-4 py-8">
          <Card className="mx-auto max-w-md">
            <h1 className="text-responsive-xl font-bold mb-4 text-gray-900">
              Verify your number
            </h1>

            <p className="text-gray-600 mb-6 sm:mb-8 text-sm sm:text-base">
              We've sent a 4-digit verification code to{' '}
              <span className="text-green-600 font-medium">+91 {phoneNumber}</span>
            </p>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* OTP Input */}
              <div className="space-y-4">
                <label className="block text-sm font-medium text-gray-700">
                  Enter verification code
                </label>
                <div className="flex gap-3 justify-center">
                  {otp.map((digit, index) => (
                    <input
                      key={index}
                      id={`otp-${index}`}
                      type="text"
                      inputMode="numeric"
                      maxLength={1}
                      value={digit}
                      onChange={(e) => handleOTPChange(index, e.target.value)}
                      onKeyDown={(e) => handleKeyDown(index, e)}
                      className="
                        w-14 h-14 sm:w-16 sm:h-16
                        text-center text-xl sm:text-2xl font-bold
                        bg-white border-2 border-gray-300 rounded-lg
                        text-gray-900 placeholder-gray-400
                        focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent
                        transition-colors duration-200
                        touch-target
                      "
                    />
                  ))}
                </div>
              </div>

              <div className="space-y-4">
                <Button
                  type="submit"
                  variant="accent"
                  size="lg"
                  fullWidth
                >
                  Verify OTP
                </Button>

                <Button
                  type="button"
                  variant="secondary"
                  size="md"
                  fullWidth
                  onClick={handleResendOTP}
                  loading={isResending}
                >
                  Resend OTP
                </Button>
              </div>
            </form>
          </Card>
        </MobileContainer>
      </div>
    </ResponsiveLayout>
  );
};

export default VerifyPhoneOTP;
