import React from 'react';
import { useMediaQuery } from 'react-responsive';

interface PageWrapperProps {
  children: React.ReactNode;
  hideBackOnDesktop?: boolean;
}

const PageWrapper: React.FC<PageWrapperProps> = ({ 
  children, 
  hideBackOnDesktop = true 
}) => {
  const isTabletOrDesktop = useMediaQuery({ minWidth: 768 });

  return (
    <div className="min-h-screen bg-primary text-white w-full">
      {/* Desktop/Tablet Container with max-width and center alignment */}
      <div className="
        w-full h-full
        md:flex md:justify-center
        md:py-8
      ">
        {/* Content Container with fixed width on larger screens */}
        <div className="
          w-full h-full
          md:w-[768px]
          lg:w-[1024px]
          md:px-6
          relative
        ">
          {/* Main content area with background and rounded corners on desktop */}
          <div className="
            w-full h-full
            md:bg-gray-700
            md:rounded-xl
            md:shadow-xl
            md:overflow-hidden
          ">
            {/* Hide back button on desktop if specified */}
            {(!isTabletOrDesktop || !hideBackOnDesktop) && (
              <div className="p-4">
                {/* Your existing back button component can go here */}
              </div>
            )}

            {/* Content with padding */}
            <div className="
              p-4
              md:p-8
              md:max-h-[calc(100vh-4rem)]
              md:overflow-y-auto
            ">
              {children}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PageWrapper;
