import React from 'react';

interface PageWrapperProps {
  children: React.ReactNode;
  hideBackOnDesktop?: boolean;
}

const PageWrapper: React.FC<PageWrapperProps> = ({ 
  children, 
  hideBackOnDesktop = true 
}) => {
  return (
    <div className="min-h-screen bg-primary text-white w-full">
      {/* Desktop/Tablet Container */}
      <div className="
        w-full 
        md:flex md:justify-center 
        md:px-4 
        lg:px-6
      ">
        {/* Content Container - Limits width on larger screens */}
        <div className="
          w-full
          md:max-w-[768px]
          lg:max-w-[1024px]
          relative
        ">
          {/* Hide back button on desktop if specified */}
          <div className={`${hideBackOnDesktop ? 'md:hidden' : ''}`}>
            {/* Your existing back button component can go here */}
          </div>

          {/* Main content */}
          <div className="
            w-full
            md:mt-8
            md:rounded-lg
            md:shadow-lg
            md:bg-primary
            md:p-6
            lg:p-8
          ">
            {children}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PageWrapper;
