import React, { useEffect, useState } from 'react';

interface LayoutProps {
  children: React.ReactNode;
  maxWidth?: string;
  showSafeArea?: boolean;
  className?: string;
}

// Simple device detection hook
const useIsMobile = () => {
  const [isMobile, setIsMobile] = useState(false);
  const [isIOS, setIsIOS] = useState(false);

  useEffect(() => {
    const checkDevice = () => {
      const width = window.innerWidth;
      const userAgent = navigator.userAgent || navigator.vendor;

      setIsMobile(width < 768);
      setIsIOS(/iPad|iPhone|iPod/.test(userAgent) || 
              (navigator.platform === 'MacIntel' && navigator.maxTouchPoints > 1));
    };

    checkDevice();
    window.addEventListener('resize', checkDevice);
    window.addEventListener('orientationchange', checkDevice);

    return () => {
      window.removeEventListener('resize', checkDevice);
      window.removeEventListener('orientationchange', checkDevice);
    };
  }, []);

  return { isMobile, isIOS };
};

// Main Layout Component
export const MobileLayout: React.FC<LayoutProps> = ({
  children,
  maxWidth = '1440px',
  showSafeArea = true,
  className = ''
}) => {
  const { isMobile, isIOS } = useIsMobile();

  const containerStyle: React.CSSProperties = {
    minHeight: isMobile ? '100dvh' : '100vh',
    width: '100%',
    maxWidth: isMobile ? '100%' : maxWidth,
    margin: isMobile ? '0' : '0 auto',
    padding: isMobile ? '1rem' : '2rem',
    position: 'relative',
    overflowX: 'hidden',
    WebkitOverflowScrolling: 'touch',
    // iOS Safe Area Support
    paddingTop: (showSafeArea && isIOS) ? 'max(1rem, env(safe-area-inset-top))' : undefined,
    paddingBottom: (showSafeArea && isIOS) ? 'max(1rem, env(safe-area-inset-bottom))' : undefined,
    paddingLeft: (showSafeArea && isIOS) ? 'max(1rem, env(safe-area-inset-left))' : undefined,
    paddingRight: (showSafeArea && isIOS) ? 'max(1rem, env(safe-area-inset-right))' : undefined,
  };

  return (
    <div 
      className={`mobile-layout ${className}`}
      style={containerStyle}
    >
      {children}
    </div>
  );
};

// Touch-friendly Button Component
interface ButtonProps {
  children: React.ReactNode;
  onClick?: () => void;
  variant?: 'primary' | 'secondary' | 'ghost';
  size?: 'small' | 'medium' | 'large';
  fullWidth?: boolean;
  disabled?: boolean;
  className?: string;
  style?: React.CSSProperties;
}

export const TouchButton: React.FC<ButtonProps> = ({
  children,
  onClick,
  variant = 'primary',
  size = 'medium',
  fullWidth = false,
  disabled = false,
  className = '',
  style = {}
}) => {
  const { isMobile } = useIsMobile();

  const getVariantStyles = () => {
    switch (variant) {
      case 'primary':
        return {
          backgroundColor: disabled ? '#6b7280' : '#00fb57',
          color: disabled ? '#9ca3af' : '#000',
          border: 'none'
        };
      case 'secondary':
        return {
          backgroundColor: disabled ? '#374151' : '#fff',
          color: disabled ? '#6b7280' : '#000',
          border: '1px solid #d1d5db'
        };
      case 'ghost':
        return {
          backgroundColor: 'transparent',
          color: disabled ? '#6b7280' : '#fff',
          border: '1px solid rgba(255, 255, 255, 0.2)'
        };
      default:
        return {};
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'small':
        return { padding: '8px 16px', fontSize: '14px' };
      case 'large':
        return { padding: '16px 32px', fontSize: '18px' };
      default:
        return { padding: '12px 24px', fontSize: '16px' };
    }
  };

  const buttonStyle: React.CSSProperties = {
    minHeight: '48px',
    minWidth: '48px',
    borderRadius: '8px',
    fontWeight: '500',
    cursor: disabled ? 'not-allowed' : 'pointer',
    width: fullWidth ? '100%' : 'auto',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '8px',
    transition: 'all 0.2s ease',
    userSelect: 'none',
    WebkitUserSelect: 'none',
    touchAction: 'manipulation',
    WebkitTapHighlightColor: 'transparent',
    ...getSizeStyles(),
    ...getVariantStyles(),
    ...style
  };

  return (
    <button
      className={`touch-button ${className}`}
      style={buttonStyle}
      onClick={onClick}
      disabled={disabled}
      onMouseEnter={(e) => {
        if (!isMobile && !disabled) {
          e.currentTarget.style.transform = 'translateY(-1px)';
          e.currentTarget.style.boxShadow = '0 4px 12px rgba(0, 0, 0, 0.15)';
        }
      }}
      onMouseLeave={(e) => {
        if (!isMobile) {
          e.currentTarget.style.transform = 'none';
          e.currentTarget.style.boxShadow = 'none';
        }
      }}
    >
      {children}
    </button>
  );
};

// Responsive Grid Component
interface GridProps {
  children: React.ReactNode;
  columns?: { mobile?: number; tablet?: number; desktop?: number };
  gap?: string;
  className?: string;
}

export const ResponsiveGrid: React.FC<GridProps> = ({
  children,
  columns = { mobile: 1, tablet: 2, desktop: 3 },
  gap = '1rem',
  className = ''
}) => {
  const { isMobile } = useIsMobile();
  const [screenWidth, setScreenWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => setScreenWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const getColumns = () => {
    if (screenWidth < 768) return columns.mobile || 1;
    if (screenWidth < 1024) return columns.tablet || 2;
    return columns.desktop || 3;
  };

  const gridStyle: React.CSSProperties = {
    display: 'grid',
    gridTemplateColumns: `repeat(${getColumns()}, minmax(0, 1fr))`,
    gap,
    width: '100%'
  };

  return (
    <div className={`responsive-grid ${className}`} style={gridStyle}>
      {children}
    </div>
  );
};

export default MobileLayout;
