import React, { useState } from 'react';
import ResponsiveLayout, { MobileContainer, Card, FormInput, Button } from '../../components/ResponsiveLayout';

interface PageProps {
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
}

const PhoneNumberEntry: React.FC<PageProps> = ({ showNotification }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [invitationCode, setInvitationCode] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (phoneNumber.length !== 10) {
      showNotification('Please enter a valid 10-digit phone number', 'error');
      return;
    }

    try {
      const response = await fetch(
        `https://api.fundos.services/api/v0/test/user/phone/otp/send?phone_number=${phoneNumber}&invite_code=${invitationCode}`
      );
      const data = await response.json();

      if (data.success) {
        localStorage.setItem('phoneNumber', phoneNumber);
        localStorage.setItem('invitationCode', invitationCode);
        if (data.user_id) {
          localStorage.setItem('userId', data.user_id);
        }
        window.history.pushState({}, '', '/verify-phone-otp');
        window.location.reload();
      } else {
        showNotification(data.message || 'Failed to send OTP. Please try again.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification('Failed to send OTP. Please check your internet connection.', 'error');
    }
  };

  const handlePhoneNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '');
    if (value.length <= 10) {
      setPhoneNumber(value);
    }
  };

  return (
    <ResponsiveLayout fullHeight padding="none" backgroundType="solid">
      <div className="full-width-mobile flex items-center justify-center bg-gray-50">
        <MobileContainer maxWidth="full" className="w-full px-4 py-8">
          <Card className="mx-auto max-w-md">
            <h1 className="text-responsive-xl font-bold mb-4 text-gray-900">
              Enter phone number
            </h1>

            <p className="text-green-600 mb-6 sm:mb-8 text-sm sm:text-base leading-relaxed">
              Your number helps us verify your identity and keep your account secure.
            </p>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Phone Number Input */}
              <div className="space-y-4">
                <FormInput
                  label="Phone Number"
                  type="tel"
                  value={phoneNumber}
                  onChange={handlePhoneNumberChange}
                  placeholder="Enter 10-digit number"
                  required
                  leftIcon={
                    <div className="flex items-center">
                      <span className="text-gray-700 text-sm font-medium">+91</span>
                      <div className="w-px h-4 bg-gray-300 mx-2" />
                    </div>
                  }
                />

                <FormInput
                  label="Invitation Code (Optional)"
                  type="text"
                  value={invitationCode}
                  onChange={(e) => setInvitationCode(e.target.value)}
                  placeholder="Enter invitation code"
                />
              </div>

              <Button
                type="submit"
                variant="accent"
                size="lg"
                fullWidth
              >
                Send OTP
              </Button>
            </form>
          </Card>
        </MobileContainer>
      </div>
    </ResponsiveLayout>
  );
};

export default PhoneNumberEntry;
