import React, { useState, useEffect } from 'react';
import Container from '../../components/Container';

interface Deal {
  deal_id: string;
  description: string;
  title: string;
  current_valuation: number;
  round_size: number;
  minimum_investment: number;
  commitment: number;
  instruments: string;
  fund_raised_till_now: number;
  logo_url: string;
  management_fee: number;
  company_stage: string;
  carry: number;
  business_model: string;
}

interface DealDetailsProps {
  dealId: string;
  showNotification: (message: string, type?: 'success' | 'error' | 'info') => void;
  onBack: () => void;
  onCommit: (dealId: string) => void;
}

const DealDetails: React.FC<DealDetailsProps> = ({ dealId, showNotification, onBack, onCommit }) => {
  const [deal, setDeal] = useState<Deal | null>(null);
  const [loading, setLoading] = useState(true);
  const [isDescriptionExpanded, setIsDescriptionExpanded] = useState(false);

  useEffect(() => {
    const fetchDealDetails = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://api.fundos.services/api/v1/live/deals/?deal_id=${dealId}`);

        if (!response.ok) {
          throw new Error('Failed to fetch deal details');
        }

        const data = await response.json();
        setDeal(data);
      } catch (error) {
        console.error('Error fetching deal details:', error);
        showNotification('Failed to load deal details. Please try again.', 'error');
      } finally {
        setLoading(false);
      }
    };

    if (dealId) {
      fetchDealDetails();
    }
  }, [dealId, showNotification]);

  const convertToCrores = (value: number) => {
    return `${(value / 10000000).toFixed(2)} Cr`;
  };

  const handleCommit = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        showNotification('User not authenticated', 'error');
        return;
      }

      // Check MCA status first
      const mcaResponse = await fetch(`https://api.fundos.services/api/v1/live/deals/mca/status?user_id=${userId}`);

      if (!mcaResponse.ok) {
        throw new Error('Failed to check MCA status');
      }

      const mcaData = await mcaResponse.json();

      if (mcaData?.message) {
        showNotification(mcaData.message, 'success');
      }

      if (mcaData.sent_status === 'success' && mcaData.request_status === 'completed') {
        onCommit(dealId);
      } else if (mcaData.sent_status === 'success' && mcaData.request_status === 'inprogress') {
        showNotification('Signing Docs in progress. Please contact admin.', 'error');
      } else {
        showNotification('Ask your fund manager to sign the docs!', 'error');
      }
    } catch (error) {
      console.error('Error in commit process:', error);
      showNotification('Failed to apply for declarations. Please try again.', 'error');
    }
  };

  const handleNotInterested = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        showNotification('User not authenticated', 'error');
        return;
      }

      const response = await fetch(`https://api.fundos.services/api/v1/live/deals/interaction?deal_id=${dealId}&user_id=${userId}&not_interested=true`, {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error('Failed to set not interested');
      }

      const data = await response.json();

      if (data?.message) {
        showNotification(data.message, 'success');
      }

      onBack(); // Go back to dashboard
    } catch (error) {
      console.error('Error setting not interested:', error);
      showNotification('Failed to set not interested. Please try again.', 'error');
    }
  };

  const toggleDescription = () => {
    setIsDescriptionExpanded(!isDescriptionExpanded);
  };

  if (loading) {
    return (
      <Container>
        <div className="flex items-center justify-center p-8">
          <div className="w-full max-w-[400px] bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-8 backdrop-blur-lg text-center">
            <div className="w-[60px] h-[60px] border-4 border-gray-700 border-t-accent rounded-full animate-spin mx-auto mb-5"></div>
            <h2 className="text-2xl font-medium text-white mb-2">Loading Deal Details</h2>
            <p className="text-sm text-gray-400">Please wait while we fetch the deal information...</p>
          </div>
        </div>
      </Container>
    );
  }

  if (!deal) {
    return (
      <Container>
        <div className="flex items-center justify-center p-8">
          <div className="w-full max-w-[400px] bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-8 backdrop-blur-lg text-center">
            <h2 className="text-2xl font-medium text-white mb-2">Deal Not Found</h2>
            <p className="text-sm text-gray-400">We couldn't find the deal you're looking for.</p>
            <button
              onClick={onBack}
              className="mt-6 w-full bg-accent text-primary font-medium py-3 px-6 rounded-lg hover:bg-opacity-90 transition-colors"
            >
              Go Back
            </button>
          </div>
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <div className="p-4">
        <button
          onClick={onBack}
          className="text-gray-400 text-2xl mb-6 hover:text-white transition-colors"
        >
          ‚Üê
        </button>

        <div className="bg-opacity-5 bg-white border border-white border-opacity-10 rounded-xl p-6 backdrop-blur-lg">
          <div className="flex items-center gap-4 mb-6">
            {deal.logo_url && (
              <img
                src={deal.logo_url}
                alt={deal.title}
                className="w-16 h-16 rounded-lg object-cover"
              />
            )}
            <div>
              <h1 className="text-2xl font-bold text-white">{deal.title}</h1>
              <p className="text-gray-400">{deal.company_stage}</p>
            </div>
          </div>

          <div className="space-y-4">
            <div>
              <h3 className="text-gray-400 text-sm mb-1">Description</h3>
              <p className={`text-white ${!isDescriptionExpanded ? 'line-clamp-3' : ''}`}>
                {deal.description}
              </p>
              {deal.description.length > 150 && (
                <button
                  onClick={toggleDescription}
                  className="text-accent text-sm mt-2"
                >
                  {isDescriptionExpanded ? 'Show less' : 'Read more'}
                </button>
              )}
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <h3 className="text-gray-400 text-sm mb-1">Valuation</h3>
                <p className="text-white font-medium">{convertToCrores(deal.current_valuation)}</p>
              </div>
              <div>
                <h3 className="text-gray-400 text-sm mb-1">Round Size</h3>
                <p className="text-white font-medium">{convertToCrores(deal.round_size)}</p>
              </div>
              <div>
                <h3 className="text-gray-400 text-sm mb-1">Min Investment</h3>
                <p className="text-white font-medium">{convertToCrores(deal.minimum_investment)}</p>
              </div>
              <div>
                <h3 className="text-gray-400 text-sm mb-1">Raised</h3>
                <p className="text-white font-medium">{convertToCrores(deal.fund_raised_till_now)}</p>
              </div>
            </div>

            <div>
              <h3 className="text-gray-400 text-sm mb-1">Business Model</h3>
              <p className="text-white">{deal.business_model}</p>
            </div>

            <div>
              <h3 className="text-gray-400 text-sm mb-1">Investment Terms</h3>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-gray-400 text-sm">Management Fee</p>
                  <p className="text-white">{deal.management_fee}%</p>
                </div>
                <div>
                  <p className="text-gray-400 text-sm">Carry</p>
                  <p className="text-white">{deal.carry}%</p>
                </div>
              </div>
            </div>
          </div>

          <div className="flex gap-4 mt-8">
            <button
              onClick={handleCommit}
              className="flex-1 bg-accent text-primary font-medium py-3 px-6 rounded-lg hover:bg-opacity-90 transition-colors"
            >
              Commit to Invest
            </button>
            <button
              onClick={handleNotInterested}
              className="flex-1 bg-opacity-10 bg-white text-white font-medium py-3 px-6 rounded-lg hover:bg-opacity-20 transition-colors"
            >
              Not Interested
            </button>
          </div>
        </div>
      </div>
    </Container>
  );
};

export default DealDetails;
