import React, { ReactNode } from 'react';

interface ResponsiveLayoutProps {
  children: ReactNode;
  className?: string;
  fullHeight?: boolean;
  centered?: boolean;
  padding?: 'none' | 'sm' | 'md' | 'lg';
  backgroundType?: 'solid' | 'gradient' | 'transparent';
}

const ResponsiveLayout: React.FC<ResponsiveLayoutProps> = ({
  children,
  className = '',
  fullHeight = true,
  centered = false,
  padding = 'md',
  backgroundType = 'solid'
}) => {
  const getPaddingClasses = () => {
    switch (padding) {
      case 'none': return '';
      case 'sm': return 'p-2 sm:p-4';
      case 'md': return 'p-4 sm:p-6 md:p-8';
      case 'lg': return 'p-6 sm:p-8 md:p-12';
      default: return 'p-4 sm:p-6 md:p-8';
    }
  };

  const getBackgroundClasses = () => {
    switch (backgroundType) {
      case 'gradient': return 'bg-gradient-to-br from-primary via-gray-900 to-primary';
      case 'transparent': return 'bg-transparent';
      case 'solid':
      default: return 'bg-primary';
    }
  };

  const baseClasses = `
    mobile-container
    ${fullHeight ? 'min-h-dvh' : 'min-h-screen'}
    ${centered ? 'flex flex-col items-center justify-center' : ''}
    ${getBackgroundClasses()}
    ${getPaddingClasses()}
    text-white
    overflow-x-hidden
    ios-scroll-fix
    ${className}
  `.trim().replace(/\s+/g, ' ');

  return (
    <>
      {/* iOS Status Bar Background */}
      <div className="ios-status-bar-bg md:hidden" />

      {/* Main Content */}
      <div className={baseClasses}>
        {children}
      </div>
    </>
  );
};

// Mobile-first container for content sections
interface MobileContainerProps {
  children: ReactNode;
  className?: string;
  maxWidth?: 'sm' | 'md' | 'lg' | 'xl' | '2xl' | 'full';
}

export const MobileContainer: React.FC<MobileContainerProps> = ({
  children,
  className = '',
  maxWidth = 'md'
}) => {
  const getMaxWidthClass = () => {
    switch (maxWidth) {
      case 'sm': return 'max-w-sm';
      case 'md': return 'max-w-md';
      case 'lg': return 'max-w-lg';
      case 'xl': return 'max-w-xl';
      case '2xl': return 'max-w-2xl';
      case 'full': return 'max-w-full';
      default: return 'max-w-md';
    }
  };

  return (
    <div className={`w-full ${getMaxWidthClass()} mx-auto ${className}`}>
      {children}
    </div>
  );
};

// Back button component
interface BackButtonProps {
  onClick: () => void;
  className?: string;
}

export const BackButton: React.FC<BackButtonProps> = ({ onClick, className = '' }) => {
  return (
    <button
      onClick={onClick}
      className={`
        touch-target 
        mb-4 
        text-gray-400 
        hover:text-white 
        transition-colors 
        duration-200
        focus-visible
        ${className}
      `}
      aria-label="Go back"
    >
      <svg 
        className="w-6 h-6" 
        fill="none" 
        stroke="currentColor" 
        viewBox="0 0 24 24"
      >
        <path 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          strokeWidth={2} 
          d="M15 19l-7-7 7-7" 
        />
      </svg>
    </button>
  );
};

// Card component for form sections
interface CardProps {
  children: ReactNode;
  className?: string;
  padding?: 'sm' | 'md' | 'lg';
}

export const Card: React.FC<CardProps> = ({ 
  children, 
  className = '', 
  padding = 'md' 
}) => {
  const getPaddingClass = () => {
    switch (padding) {
      case 'sm': return 'p-4';
      case 'md': return 'p-6 sm:p-8';
      case 'lg': return 'p-8 sm:p-10';
      default: return 'p-6 sm:p-8';
    }
  };

  return (
    <div className={`card-base ${getPaddingClass()} ${className}`}>
      {children}
    </div>
  );
};

// Form input component
interface FormInputProps {
  label: string;
  type?: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  placeholder?: string;
  required?: boolean;
  className?: string;
  leftIcon?: ReactNode;
  rightIcon?: ReactNode;
}

export const FormInput: React.FC<FormInputProps> = ({
  label,
  type = 'text',
  value,
  onChange,
  placeholder,
  required = false,
  className = '',
  leftIcon,
  rightIcon
}) => {
  return (
    <div className={`mb-4 sm:mb-6 ${className}`}>
      <label className="block mb-2 text-sm font-medium text-gray-300">
        {label}
        {required && <span className="text-error ml-1">*</span>}
      </label>
      <div className="relative">
        {leftIcon && (
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            {leftIcon}
          </div>
        )}
        <input
          type={type}
          value={value}
          onChange={onChange}
          placeholder={placeholder}
          required={required}
          className={`
            input-base
            ${leftIcon ? 'pl-10' : ''}
            ${rightIcon ? 'pr-10' : ''}
          `}
        />
        {rightIcon && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            {rightIcon}
          </div>
        )}
      </div>
    </div>
  );
};

// Primary button component
interface ButtonProps {
  children: ReactNode;
  onClick?: () => void;
  type?: 'button' | 'submit' | 'reset';
  variant?: 'primary' | 'secondary' | 'accent';
  size?: 'sm' | 'md' | 'lg';
  fullWidth?: boolean;
  disabled?: boolean;
  loading?: boolean;
  className?: string;
}

export const Button: React.FC<ButtonProps> = ({
  children,
  onClick,
  type = 'button',
  variant = 'primary',
  size = 'md',
  fullWidth = false,
  disabled = false,
  loading = false,
  className = ''
}) => {
  const getSizeClasses = () => {
    switch (size) {
      case 'sm': return 'px-4 py-2 text-sm';
      case 'md': return 'px-6 py-3 text-base';
      case 'lg': return 'px-8 py-4 text-lg';
      default: return 'px-6 py-3 text-base';
    }
  };

  const getVariantClasses = () => {
    switch (variant) {
      case 'primary': return 'btn-primary';
      case 'secondary': return 'btn-secondary';
      case 'accent': return 'btn-accent';
      default: return 'btn-primary';
    }
  };

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled || loading}
      className={`
        ${getVariantClasses()}
        ${getSizeClasses()}
        ${fullWidth ? 'w-full' : ''}
        ${disabled || loading ? 'opacity-50 cursor-not-allowed' : ''}
        focus-visible
        ${className}
      `}
    >
      {loading ? (
        <div className="flex items-center justify-center">
          <div className="loading-spinner w-5 h-5 mr-2" />
          Loading...
        </div>
      ) : (
        children
      )}
    </button>
  );
};

export default ResponsiveLayout;
